document.addEventListener("DOMContentLoaded", function() {
  var e = document.querySelector(".contacts-button"),
    t = document.querySelector(".feedback-close");
  e.addEventListener("click", e => {
    e.preventDefault(),
      document.querySelector(".feedback-container").classList.add("form-open"),
      setTimeout(function() {
        document
          .querySelector(".feedback-container")
          .classList.remove("form-open");
      }, 900),
      document.querySelector(".feedback-container").classList.add("form-ever");
  }),
    t.addEventListener("click", e => {
      document
        .querySelector(".feedback-container")
        .classList.remove("form-ever");
    });
  var c = document.querySelector("[name=name]"),
    n = document.querySelector("[name=email]"),
    a = document.querySelector("[name=text]");
  document.querySelector(".feedback").addEventListener("submit", function(e) {
    (c.value && n.value && a.value.trim()) ||
      (e.preventDefault(),
      document.querySelector(".feedback-container").classList.add("inv-form"),
      c.value ? c.classList.remove("invalid") : c.classList.add("invalid"),
      n.value ? n.classList.remove("invalid") : n.classList.add("invalid"),
      a.value.trim()
        ? a.classList.remove("invalid")
        : a.classList.add("invalid")),
      setTimeout(function() {
        document
          .querySelector(".feedback-container")
          .classList.remove("inv-form");
      }, 1e3);
  });
});
