document.addEventListener("DOMContentLoaded", function() {
  var e = document.getElementById("slideOne"),
    t = document.getElementById("slideTwo"),
    s = document.getElementById("slideThree");
  e.addEventListener("click", l => {
    for (
      var i = document.getElementsByClassName("feature-slider-item"), d = 0;
      d < i.length;
      d++
    )
      i[d].style.left = "0";
    s.classList.remove("active-slider-button"),
      t.classList.remove("active-slider-button"),
      e.classList.add("active-slider-button");
  }),
    t.addEventListener("click", l => {
      for (
        var i = document.getElementsByClassName("feature-slider-item"), d = 0;
        d < i.length;
        d++
      )
        i[d].style.left = "-1140px";
      e.classList.remove("active-slider-button"),
        s.classList.remove("active-slider-button"),
        t.classList.add("active-slider-button");
    }),
    s.addEventListener("click", l => {
      for (
        var i = document.getElementsByClassName("feature-slider-item"), d = 0;
        d < i.length;
        d++
      )
        i[d].style.left = "-2283px";
      t.classList.remove("active-slider-button"),
        e.classList.remove("active-slider-button"),
        s.classList.add("active-slider-button");
    });
});
