ymaps.ready(function() {
  var e = new ymaps.Map(
      "map",
      { center: [59.939199, 30.321605], zoom: 17, controls: [] },
      { searchControlProvider: "yandex#search" }
    ),
    o = ymaps.templateLayoutFactory.createClass(
      '<div style="color: #FFFFFF; font-weight: bold;">$[properties.iconContent]</div>'
    ),
    t = new ymaps.Placemark(
      [59.938631, 30.323055],
      {
        hintContent: "Собственный значок метки с контентом",
        balloonContent:
          "Nёrds design studio<br>Мир никогда не будет прежним после того как увидит Ваш сайт!",
        iconContent: "12"
      },
      {
        iconLayout: "default#imageWithContent",
        iconImageHref: "img/map-marker.png",
        iconImageSize: [231, 190],
        iconImageOffset: [-50, -190],
        iconContentLayout: o
      }
    );
  e.geoObjects.add(t);
});
